import React, { useState, useEffect } from 'react';
import Record from '../components/Record';
import SetPasscodeModal from '../components/ActivateModal';

const ActivatePage = () => {
    const [ isModalOpen, setIsModalOpen] = useState(false);
    const [ activate, setActivate ] = useState(false);
    
  const [isCharging, setIsCharging] = useState(false);
  const [ zoomIn, setZoomIn ] = useState(false)
  const [robbed, setRobbed] = useState(false);

  useEffect(() => {
      if (robbed) {
          // Flash red for a brief moment and then fade out
          const timeoutId = setTimeout(() => {
              setRobbed(false);
          }, 500); // The flash duration could be 500ms

          return () => clearTimeout(timeoutId);
      }
  }, [robbed]);
    useEffect(() => {
        setTimeout(() => {
          setZoomIn(true);
        }, 500);
      
        let batteryManager;
    
        async function getBattery() {
          try {
            batteryManager = await navigator.getBattery();
            setIsCharging(batteryManager.charging);
    
            // Define the event listener function
            const handleChargingChange = () => {
              setRobbed(true);
              
              setIsCharging(batteryManager.charging);
            };
    
            // Add event listener
            batteryManager.addEventListener('chargingchange', handleChargingChange);
    
            // Remove event listener on cleanup
            return () => {
              batteryManager.removeEventListener('chargingchange', handleChargingChange);
            };
          } catch (error) {
            console.error("Battery Status API is not supported on this platform.");
          }
        }
    
        getBattery();
    
        // Cleanup function to remove the event listener
        return () => {
          if (batteryManager) {
            batteryManager.removeEventListener('chargingchange', getBattery);
          }
        };
      }, []);

    useEffect(() => {

        window.addEventListener('beforeunload', function (e) {
            // Cancel the event
            e.preventDefault(); // If you prevent default behavior in older browsers
            e.returnValue = ''; // Chrome requires returnValue to be set
        });
        
    }, [activate]);


    const openModal = () => {
        setIsModalOpen(true);
        console.log("test")
    };
    const closeModal = () => {
        console.log('hey');
        setIsModalOpen(false);
    };

    return (
        <div
            className="relative w-full h-screen bg-black flex flex-col items-center justify-center"
            onClick={() => !isModalOpen && openModal()}
        >
          { zoomIn ? <Record style={{ transform: 'scale(1) rotate(90deg)', transition: 'transform 0.3s ease-in-out',}}/> : <Record style={{transform: 'scale(0.25) rotate(270deg)',transition: 'transform 0.3s ease-in-out',}} />}
            <div className="text-white">Click anywhere to activate</div>
            <button onClick={openModal}>Button</button>
            <SetPasscodeModal isOpen={isModalOpen} onClose={closeModal} />

             {/* Red Flash Overlay */}
             {robbed && (
                <div
                    style={{
                        position: 'fixed',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'red',
                        opacity: 0.5,
                        transition: 'opacity 0.5s ease-in-out',
                    }}
                    onAnimationEnd={() => setRobbed(false)}
                />
            )}
        </div>
    );
};

export default ActivatePage;
