import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerPhoneNumber } from '../lib/api/user';
import Record from '../components/Record';

const PhoneNumPage = () => {
    const [loaded, setLoaded] = useState(false);
    const [complete, setComplete] = useState(false);
    const [phoneNumber, setPhoneNumber] = useState('');

    const formRef = useRef([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Initialize refs for n elements for digits
        for (let i = 0; i < 10; i++) {
            formRef.current[i] = formRef.current[i] || React.createRef();
        }
        setLoaded(true);
    }, []);

    useEffect(() => {
        window.addEventListener('chargingchange', (event) => {
            if (!event.target.charging) {
                console.log("ALERT NOW");
            }
        });

        window.addEventListener('beforeunload', function (e) {
            // Cancel the event
            e.preventDefault(); // If you prevent default behavior in older browsers
            e.returnValue = ''; // Chrome requires returnValue to be set
        });
        
    }, [activate]);

    const handlePrevNext = (e, index) => {
        e.preventDefault();

        if (!/\d/.test(e.key)) {
            //input is not a number
            e.preventDefault();
            if (e.key === 'Backspace') {
                if (complete) {
                    setComplete(false);
                }
                setPhoneNumber(
                    phoneNumber.substring(0, phoneNumber.length - 1),
                );
                formRef.current[index].current.innerText = '';
                const prevElement = formRef.current[index - 1];
                if (prevElement) {
                    prevElement.current.focus();
                }
            } else if (complete && e.key == 'Enter') {
                //send POST request to server
                registerPhoneNumber(phoneNumber);
                //Link to MainPage
                navigate('/activate');
            }
        } else if (!complete) {
            setPhoneNumber(phoneNumber + e.key);
            formRef.current[index].current.innerText = e.key;
            const nextElement = formRef.current[index + 1];
            if (nextElement) {
                nextElement.current.focus();
            } else {
                setComplete(true);
            }
        }
    };

    return (
        <div className="w-full h-screen bg-black flex flex-col items-center justify-center">
            <Record scale={1} rotation={90} />

            <div className="text-white">Enter Your Phone Number mf</div>
            {loaded && (
                <div className="flex">
                    {formRef.current.map((ref, index) => (
                        <span
                            key={index}
                            ref={ref}
                            className="border-b-2 border-white w-12 h-12 mr-2 flex justify-center items-center text-center text-white text-4x1"
                            contentEditable="true"
                            onKeyDown={(e) => handlePrevNext(e, index)}
                        ></span>
                    ))}
                </div>
            )}
        </div>
    );
};

export default PhoneNumPage;
