import React, { useState, useEffect } from 'react';
import Record from '../components/Record';
// import Modal from 'react-modal';
import ActivateModal from '../components/ActivateModal';

// Modal.setAppElement('#root');

const ActivatePage = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [ activate, setActivate ] = useState(false);
    
  const [isCharging, setIsCharging] = useState(false);s
    useEffect(() => {
        let batteryManager;
    
        async function getBattery() {
          try {
            batteryManager = await navigator.getBattery();
            setIsCharging(batteryManager.charging);
    
            // Define the event listener function
            const handleChargingChange = () => {
              setIsCharging(batteryManager.charging);
            };
    
            // Add event listener
            batteryManager.addEventListener('chargingchange', handleChargingChange);
    
            // Remove event listener on cleanup
            return () => {
              batteryManager.removeEventListener('chargingchange', handleChargingChange);
            };
          } catch (error) {
            console.error("Battery Status API is not supported on this platform.");
          }
        }
    
        getBattery();
    
        // Cleanup function to remove the event listener
        return () => {
          if (batteryManager) {
            batteryManager.removeEventListener('chargingchange', getBattery);
          }
        };
      }, []);

    useEffect(() => {

        window.addEventListener('beforeunload', function (e) {
            // Cancel the event
            e.preventDefault(); // If you prevent default behavior in older browsers
            e.returnValue = ''; // Chrome requires returnValue to be set
        });
        
    }, [activate]);


    const openModal = () => {
        setIsModalOpen(true);
        console.log("test")
    };
    const closeModal = () => {
        console.log('hey');
        setIsModalOpen(false);
    };

    const customStyles = {
        overlay: {
            backgroundColor: 'rgba(0, 0, 0, 0.75)', // Adjusted for a darker backdrop
        },
        content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '400px', // Adjusted width as per the design
            height: '200px', // Adjusted height as per the design
            background: '#333', // Dark background
            borderRadius: '20px', // Rounded corners
            border: '1px solid #00BFFF', // Blue border
            display: 'flex', // Flexbox for centering
            flexDirection: 'column', // Stack children vertically
            justifyContent: 'center', // Center vertically
            alignItems: 'center', // Center horizontally
            color: 'white', // White text
        },
    };

    return (
        <div
            className="relative w-full h-screen bg-black flex flex-col items-center justify-center"
            onClick={() => !isModalOpen && openModal()}
        >
            <Record scale={1} rotation={90} />
            <div className="text-white">Click anywhere to activate</div>
            <button onClick={openModal}>Button</button>
            <ActivateModal isOpen={isModalOpen} onClose={closeModal} />
        </div>
    );
};

export default ActivatePage;
