import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faQuestion } from '@fortawesome/free-solid-svg-icons';

const HelpButton = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const speechRectangleStyle = {
        position: 'absolute',
        top: '0',
        left: '100%', // Position it to the right of the button
        width: '200px', // Or any other size
        padding: '10px',
        border: '2px solid #ccc', // Updated border style
        background: 'black', // Fill the modal with black color
        color: 'white', // Set text color to white for contrast
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        borderRadius: '8px', // Updated border radius
        zIndex: 1000, // Ensure it's above other items
        display: isModalOpen ? 'block' : 'none' // Display based on isModalOpen
    };

    // Function to close the speech rectangle
    const closeSpeechRectangle = () => setIsModalOpen(false);

    return (
        <div style={{ position: 'relative' }} onMouseLeave={closeSpeechRectangle}>
            <div 
                className='w-4 h-4 border-2 rounded-md flex justify-center items-center'
                onMouseEnter={() => setIsModalOpen(true)}
            >
                <FontAwesomeIcon icon={faQuestion} className='text-white text-2x1'/>
            </div>
            <div style={speechRectangleStyle} onMouseLeave={closeSpeechRectangle}>
                4 digit code used to deactivate later
            </div>
        </div>
    );
}

export default HelpButton;
