import React, { useEffect, useRef, useState } from 'react';

function PhoneNumPage() {
    const [loaded, setLoaded] = useState(false);
    const [complete, setComplete] = useState(false);
    const [phoneNumber, setPhoneNumber] = useState('');

    const formRef = useRef([]);

    useEffect(() => {
        // Initialize refs for n elements for digits
        for (let i = 0; i < 10; i++) {
            formRef.current[i] = formRef.current[i] || React.createRef();
        }
        setLoaded(true);
    }, []);

    useEffect(() => {
        if (complete) {
            //POST request to set phone number
        }
    }, [complete]);

    const handlePrevNext = (e, index) => {
        e.preventDefault();

        if (!/\d/.test(e.key)) {    //input is non-int
            e.preventDefault();
            if (e.key === 'Backspace') {
                formRef.current[index].current.innerText = '';
                const prevElement = formRef.current[index - 1];
                if (prevElement) {
                    prevElement.current.focus();
                }
            }
        } else if (!complete) {
            setPhoneNumber(phoneNumber + e.key);
            formRef.current[index].current.innerText = e.key;
            const nextElement = formRef.current[index + 1];
            if (nextElement) {
                nextElement.current.focus();
            } else {
                setComplete(true);
            }
        }
    };

    return (
        <div className="w-full h-screen bg-black flex flex-col items-center justify-center">
            <div>
                <div className="bg-white w-[60px] h-[60px] rounded-[50%] flex justify-center items-center">
                    <div className="bg-black w-[55px] h-[55px] rounded-[50%] flex justify-center items-center">
                        <div className="bg-red-700 w-[45px] h-[45px] rounded-[50%] relative">
                            <div className="bg-red-900 w-[15px] h-[15px] rounded-[50%] absolute top-[55%] left-[20%]"></div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="text-white">Enter Your Phone Number mf</div>
            {loaded && (
                <div className="flex">
                    {formRef.current.map((ref, index) => (
                        <span
                            key={index}
                            ref={ref}
                            className="border-b-2 border-white w-12 h-12 mr-2 flex justify-center items-center text-center text-white text-4x1"
                            contentEditable="true"
                            onKeyDown={(e) => handlePrevNext(e, index)}
                        ></span>
                    ))}
                </div>
            )}
        </div>
    );
}

export default PhoneNumPage;
