import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerPhoneNumber } from '../lib/api/user';
import Record from '../components/Record';
import CustomInput from '../components/CustomInput';

const previousStyle = {
    transform: 'scale(1) rotate(90deg)',
    transition: 'transform 0.3s ease-in-out',
};

const afterStyle = {
    transform: 'scale(0.25) rotate(270deg)',
    transition: 'transform 0.3s ease-in-out',
};

const PhoneNumPage = () => {
    const [loaded, setLoaded] = useState(false);

    const [isZoomed, setIsZoomed] = useState(true);

    const formRef = useRef([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Initialize refs for n elements for digits
        for (let i = 0; i < 10; i++) {
            formRef.current[i] = formRef.current[i] || React.createRef();
        }
        setLoaded(true);

        setTimeout(() => {
            setIsZoomed(false);
        }, 2000);
    }, []);

    return (
        <div className="w-full h-screen bg-black flex flex-col items-center justify-center">
            {isZoomed ? (
                <>
                    <Record style={previousStyle} />
                    <div className=''>
                        <h1 className="text-white text-3xl">Vanguard</h1>
                    </div>
                </>
            ) : (
                <>
                    <Record style={afterStyle} />
                    <div className="text-white">Enter Your Phone Number mf</div>
                    {loaded && (
                        <CustomInput
                            formRef={formRef}
                            registerPhoneNumber={registerPhoneNumber}
                        />
                    )}
                </>
            )}
        </div>
    );
};

export default PhoneNumPage;
