import React, { useState } from 'react';

const ActivateModal = ({ isOpen, onClose }) => {
    const [passcode, setPasscode] = useState('');

    if (!isOpen) return null;

    const handleCancel = () => {
        setPasscode(''); // Optionally clear input
        onClose(); // Invoke the onClose callback
    };

    const handleStart = () => {
        // Implement what should happen when "Start" is clicked
        console.log('Input Value:', passcode);
        onClose(); // Optionally close the modal after action
    };

    return (
        <div className="modal-overlay absolute w-[100%] h-[bg-gray-500 bg-opacity-50">
            <div className="modal-content">
                <div className="modal-header">
                    <h4>Enter Details</h4>
                </div>
                <div className="modal-body">
                    <input
                        type="text"
                        value={passcode}
                        onChange={(e) => setPasscode(e.target.value)}
                        placeholder="Type here..."
                    />
                </div>
                <div className="modal-footer">
                    <button onClick={handleCancel}>Cancel</button>
                    <button onClick={handleStart}>Start</button>
                </div>
            </div>
        </div>
    );
};

export default ActivateModal;
