import React, { useState, useEffect } from 'react';
import Record from '../components/Record';
import SetPasscodeModal from '../components/SetPasscodeModal';
import CheckInModal from '../components/CheckInModal';

const ActivatePage = () => {
    const [ isModalOpen, setIsModalOpen ] = useState(false);
    
    const [ activate, setActivate ] = useState(false);
    const [ robbed, setRobbed ] = useState(false);

    const [ zoomIn, setZoomIn ] = useState(false);

    useEffect(() => {
        if (robbed) {
            // Flash red for a brief moment and then fade out
            <client className="alert"></client>
            const timeoutId = setTimeout(() => {
                setRobbed(false);
            }, 500); // The flash duration could be 500ms

            return () => clearTimeout(timeoutId);
        }
    }, [robbed]);

    useEffect(() => {
        setTimeout(() => {
            setZoomIn(true);
        }, 500);
    }, []);

    useEffect(() => {
        let batteryManager;

        async function getBattery() {
            try {
                batteryManager = await navigator.getBattery();
                batteryManager.addEventListener('chargingchange', () =>
                    setRobbed(true),
                );
            } catch (error) {
                console.error(
                    'Battery Status API is not supported on this platform.',
                );
            }
        }

        if (activate) {
            window.addEventListener('beforeunload', function (e) {
                e.preventDefault(); // If you prevent default behavior in older browsers
                e.returnValue = ''; // Chrome requires returnValue to be set
            });

            getBattery();
        }

        return () => {
            if (batteryManager) {
                batteryManager.removeEventListener('chargingchange', () =>
                    setRobbed(true),
                );
                window.removeEventListener('beforeunload', function (e) {
                    e.preventDefault();
                });
            }
        };
    }, [activate]);

    const openModal = () => {
        setIsModalOpen(true);
        console.log('test');
    };

    const closeModal = () => {
        console.log('hey');
        setIsModalOpen(false);
    };

    return (
        <div
            className="relative w-full h-screen bg-black flex flex-col items-center justify-center"
            onClick={() => {
                !isModalOpen && openModal();
            }}
        >
            {zoomIn ? (
                <Record
                    style={{
                        transform: 'scale(1) rotate(90deg)',
                        transition: 'transform 0.3s ease-in-out',
                    }}
                />
            ) : (
                <Record
                    style={{
                        transform: 'scale(0.25) rotate(270deg)',
                        transition: 'transform 0.3s ease-in-out',
                    }}
                />
            )}

            {activate ? (
                <>
                    <CheckInModal
                        isOpen={isModalOpen}
                        onClose={closeModal}
                        setActivate={setActivate}
                        setRobbed={setRobbed}
                    />
                    <div className="absolute bottom-0 left-0 text-white">
                        <h1>Recording In Process</h1>
                    </div>
                </>
            ) : (
                <>
                    <div className="text-white">Click anywhere to activate</div>

                    <SetPasscodeModal
                        isOpen={isModalOpen}
                        onClose={closeModal}
                        setActivate={setActivate}
                    />
                </>
            )}

            {/* Red Flash Overlay */}
            {robbed && (
                <div
                    style={{
                        position: 'fixed',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'red',
                        opacity: 0.5,
                        transition: 'opacity 0.5s ease-in-out',
                    }}
                    onAnimationEnd={() => setRobbed(false)}
                />
            )}
        </div>
    );
};

export default ActivatePage;
