import React, { useState, useEffect } from 'react';

const AccelerometerComponent = () => {
  const [acceleration, setAcceleration] = useState({ x: 0, y: 0, z: 0 });

  useEffect(() => {
    let accelerometer;

    const handleReading = () => {
      setAcceleration({
        x: accelerometer.x,
        y: accelerometer.y,
        z: accelerometer.z
      });
    };

    if ('Accelerometer' in window) {
      try {
        accelerometer = new Accelerometer({frequency: 60});
        accelerometer.addEventListener('reading', handleReading);
        accelerometer.start();
      } catch (error) {
        console.log('Accelerometer access denied or not supported');
      }
    }

    return () => {
      if (accelerometer) {
        accelerometer.removeEventListener('reading', handleReading);
        accelerometer.stop();
      }
    };
  }, []);

  return (
    <div>
      <p>X-axis: {acceleration.x}</p>
      <p>Y-axis: {acceleration.y}</p>
      <p>Z-axis: {acceleration.z}</p>
    </div>
  );
};

export default AccelerometerComponent;
