import React, { useState, useRef, useEffect } from 'react';
import CustomInput from './CustomInput';

const SetPasscodeModal = ({ isOpen, onClose }) => {
    const [passcode, setPasscode] = useState('');
    const [ loaded, setLoaded ] = useState(false);

    const formRef = useRef([]);

    useEffect(() => {
        // Initialize refs for n elements for digits
        for (let i = 0; i < 4; i++) {
            formRef.current[i] = formRef.current[i] || React.createRef();
        }
        setLoaded(true);
    }, []);

    if (!isOpen) {
        return null;
    }

    const handleCancel = () => {
        console.log('yo');
        setPasscode(''); // Optionally clear input
        onClose(); // Invoke the onClose callback
    };

    const handleStart = () => {
        // Implement what should happen when "Start" is clicked
        localStorage.setItem('userPasscode', passcode)
        console.log('Input Value:', passcode);
        onClose(); // Optionally close the modal after action
    };

    return (
        <div className="modal-overlay absolute w-[100%] h-[100%] flex justify-center items-center bg-gray-500 bg-opacity-50">
            <div className="modal-content text-white">
                <div className="modal-header">
                    <h4>Enter Details</h4>
                </div>
                <div className="modal-body">
                    {loaded && (
                        <CustomInput
                            formRef={formRef}
                            setPasscode={setPasscode}
                        />
                    )}
                </div>
                <div className="modal-footer flex justify-between">
                    <button className="" onClick={onClose}>
                        Cancel
                    </button>
                    <button onClick={handleStart}>Start</button>
                </div>
            </div>
        </div>
    );
};

export default SetPasscodeModal;
