import React from 'react';

const CheckInModal = ({ isOpen, onClose }) => {
    const [passcode, setPasscode] = useState('');
    const [loaded, setLoaded] = useState(false);

    const formRef = useRef([]);

    useEffect(() => {
        // Initialize refs for n elements for digits
        for (let i = 0; i < 4; i++) {
            formRef.current[i] = formRef.current[i] || React.createRef();
        }
        setLoaded(true);
    }, []);

    if (!isOpen) {
        return null;
    }

    const handleCancel = () => {
        setPasscode(''); 
        onClose();
    };
    const handleUnlock = () => {
        if (passcode.length == 4) {
            setActivate(true);
            localStorage.setItem('userPasscode', passcode);
            console.log('Input Value:', passcode);
            onClose(); 
        }
    };
    
    return (
        <div className="modal-overlay absolute w-[100%] h-[100%] flex justify-center items-center bg-gray-500 bg-opacity-50">
            <div className="modal-content text-white">
                <div className="modal-header">
                    <h4>Enter Details</h4>
                </div>
                <div className="modal-body">
                    {loaded && (
                        <CustomInput
                            formRef={formRef}
                            setPasscode={setPasscode}
                        />
                    )}
                </div>
                <div className="modal-footer flex justify-between">
                    <button className="" onClick={handleCancel}>
                        Cancel
                    </button>
                    <button onClick={handleStart}>Unlock</button>
                </div>
            </div>
        </div>
    );
};

export default CheckInModal;
